mapper.select:
select count(user_id)
from users
where user_id = #{userId}

select idx, user_id, user_pwd, refresh_token
from users
where user_id = #{userId} and user_pwd = #{userPwd}

select idx, user_id, user_pwd, user_name, email_id, email_domain, mobile, salt, join_date, position, refresh_token
from users
where user_id = #{userId}

select idx, user_id, user_pwd, user_name, email_id, email_domain, mobile, salt, join_date, position, refresh_token
from users
where email_id = #{emailId} and email_domain = #{emailDomain}

mapper.update:
update users
user_name = #{userName}, email_id = #{emailId}, email_domain = #{emailDomain}, mobile = #{mobile},
where user_id = #{userId}

mapper.select:
select count(user_id)
from users
