update users
set refresh_token = #{refreshToken}
where user_id = #{userId}

mapper.select:
select refresh_token
from users
where user_id = #{userId}

mapper.update:
update users
set refresh_token = #{refreshToken, jdbcType=VARCHAR}
where user_id = #{userId}
